INSERT INTO familles VALUES(3,'Négociant-Vinificateur',replace(replace('Opérateur dont l''activité principale est l''achat de vendanges fraîches ou de moût pour faire du vin. Il peut accessoirement acheter des matières premières pour les revendre, vinifier pour le compte d’autrui, acheter du vin en complément de gamme. \r\nÉlaborateurs de produits dérivés à partir de raisins ou moûts achetés à des récoltants.\r\nLes services en ligne des DOUANES indispensables à votre métier : SV12 / STOCK / OENO','\r',char(13)),'\n',char(10)));
INSERT INTO familles VALUES(4,'Viticulteur',replace(replace('Récoltant non vinificateur, exploitant des parcelles de vignes, cultivant en vue de la production de vin, apporteur à une cave coopérative, ou de la vente de raisin à un négociant-vinificateur.\r\nLes services en ligne des DOUANES indispensables à votre métier : PARCEL / RÉCOLTE (sans mandat à un tiers)','\r',char(13)),'\n',char(10)));
